var configs = {};
var params = require('./params.js');

var isProduction = params.isProduction;
configs.isProduction = isProduction;

// Load Enviornment Files
if (params.env_code == params.env_codes.development) {
    configs = require('./development.js');
} else if (params.env_code == params.env_codes.staging){
    configs = require('./staging.js');
} else if (params.env_code == params.env_codes.production){
    configs = require('./production.js');
} else if (params.env_code == params.env_codes.beta){
    configs = require('./beta.js');
}

configs.params = params;

module.exports = configs;